mapper.select:
SELECT
    c.course_id,
    c.course_title,
    c.course_type,
    c.course_content,
    c.created_at,
    COALESCE((
                 SELECT SUM(view_count)
                 FROM views v
                 WHERE v.board_id = c.course_id AND v.board_type = #{boardType}
             ), 0) AS total_views,
    COALESCE((
                 SELECT SUM(CASE WHEN like_count =  1 THEN 1 ELSE 0 END)
                 FROM likes l
                 WHERE l.board_id = c.course_id AND l.board_type = #{boardType}
             ), 0) AS total_likes,
    COALESCE((
                 SELECT SUM(CASE WHEN like_count = -1 THEN 1 ELSE 0 END)
                 FROM likes dl
                 WHERE dl.board_id = c.course_id AND dl.board_type = #{boardType}
             ), 0) AS total_dislikes
FROM courses c
WHERE c.course_id = #{courseId}
;


SELECT course_id, course_title,course_type, course_content, created_at
FROM courses
WHERE
${column} = #{value}

ORDER BY course_id ASC
LIMIT #{valueOfOnePage} OFFSET #{index};
